import 'package:intex_mobile_app/core/models/Order.dart';import 'package:intex_mobile_app/core/service/network_service.dart';class CartRepository {const  CartRepository();   Order addCount(Order order) {     order.count++;   return order;  }Order removeCount(Order order) {    if (order.count < 2) {      order.count--;    }    return order;  }  Future requestCall(String name, String number) async {    var response = await NetworkService.POST(        NetworkService.APi_POST_REQUEST_CALL,        {"name": name, "phone": number});    if (response.statusCode == 200 || response.statusCode == 201) {      return true;    }    return response.statusCode;  }}