import 'package:equatable/equatable.dart';import 'package:intex_mobile_app/core/models/Order.dart';import 'package:intex_mobile_app/core/service/network_service.dart';abstract class ICartRepository extends Equatable{  Future requestCall(String name, String number) async{}  Future sendOrder() async{}}class CartRepository extends ICartRepository{late Order order;  CartRepository(this.order);   void addCount() {     order.count++;  }void removeCount() {    if (order.count >= 2) {      order.count--;    }  }  @override  Future requestCall(String name, String number) async {    var response = await NetworkService.POST(        NetworkService.APi_POST_REQUEST_CALL,        {"name": name, "phone": number});    if (response.statusCode == 200 || response.statusCode == 201) {      return true;    }    return response.statusCode;  }  @override  Future sendOrder() async {    var response = await NetworkService.POST(        NetworkService.APi_POST_REQUEST_CALL,        order.toJson());    if (response.statusCode == 200 || response.statusCode == 201) {      return true;    }    return response.statusCode;  }  @override  List<Object?> get props => [order];}