import 'package:hive_flutter/hive_flutter.dart';class DBService {  static String intex = 'intex';  static String site = 'sites';  static String category = 'category';  static String lang = 'lang';  static Future<void> init() async {    await Hive.initFlutter();    await Hive.openBox(intex);  }  static void setData<T>(String key, dynamic value) {    var box = Hive.box(intex);    box.put(key, value);  }  static void editData<T>(String key, T value, [int index = 0]) {    var box = Hive.box(intex);    box.put(key, value);  }  static getData<T>(String key) {    if(key == lang){      var box = Hive.box(intex);      return box.get(lang,defaultValue: "uz");    }    var box = Hive.box(intex);    return box.get(key, defaultValue: null);  }  //  // static void deleteData(String key) {  //   _box!.delete(key);  // }  //  // static void clear() {  //   _box!.clear();  // }  //  // static void close() {  //   _box!.close();  // }}