import 'dart:convert';import 'package:dio/dio.dart';import 'package:intex_mobile_app/core/service/interseptor.dart';// ignore_for_file: non_constant_identifier_namesclass NetworkService {  /// Base Url */  static bool isTester = true;   static String SERVER_DEVELOPMENT = "http://45.95.203.171";   static String SERVER_PRODUCTION = "http://45.95.203.171";  static String get baseApiUrl {    if (isTester) return SERVER_DEVELOPMENT;    return SERVER_PRODUCTION;  }  /// Apis */  static String API_LIST = "";  // {ID}  /// Headers */  static Map<String, String> get headers {    Map<String, String> headers = {       'Accept': '*/*',      'Content-Type': 'application/json',      // 'Accept-Version': 'api',    };    return headers;  }  static Map<String, String> get headersMultipart {    Map<String, String> headers = {      // 'Accept': '*/*',      // 'Content-Type': 'multipart/form-data',      // 'Accept-Version': 'api/site',    };    return headers;  }  /// BaseOptions */  static final BaseOptions _baseDioOptions = BaseOptions(    baseUrl: baseApiUrl,    headers: headers,    connectTimeout: 40000,    receiveTimeout: 40000,    contentType: 'application/json',    responseType: ResponseType.json,  );  static final Dio _dio = Dio(_baseDioOptions)..interceptors.add(DioInterceptor());  /* Dio Requests */  static Future GET(String api, Map<String, dynamic> params) async {    Response response = await _dio.get(api, queryParameters: params);    if (response.statusCode == 200 || response.statusCode == 201) {      return response.data;    }    return null;  }  static Future POST(String api, Map<String, dynamic> body) async {    Response response = await _dio.post(api, data: jsonEncode(body) );    if (response.statusCode == 200 || response.statusCode == 201) {      return response.data;    }    return response.statusCode;  }  static Future<Map<String, dynamic>?> PUT(String api, Map<String, dynamic> params) async {    Response response = await _dio.put(api, data: params); // http or https    if (response.statusCode == 200 || response.statusCode == 201) {      return response.data;    }    return null;  }  /// Dio Query Params */  static Map<String, String> paramsEmpty() {    Map<String, String> params = {};    return params;  }  /// Dio Body */  static Map<String, dynamic> bodyCreate(dynamic post) {    Map<String, dynamic> params = {};    params.addAll({      'title': post.title,      'body': post.body,      'userId': post.userId,    });    return params;  }  /// Dio Parsing */  static List<dynamic> parsePostList(List json) {    //  var data = List<dynamic>.from(json.map((x) => dynamic.fromJson    //  (x)));    return [];  }}