import 'package:url_launcher/url_launcher.dart';class URL{ static Future<void> launch(String url) async {    if (!await launchUrl(      Uri.parse(url),      mode: LaunchMode.inAppWebView,      webViewConfiguration: const WebViewConfiguration(enableDomStorage: false),    )) {      throw 'Could not launch $url';    }  }static Future<void> launchPhoneCall(String phoneNumber) async {   final Uri launchUri = Uri(     scheme: 'tel',     path: "+998$phoneNumber",   );   await launchUrl(launchUri); }}