import 'package:flutter/material.dart';class CustomButton extends StatelessWidget {  final title;  final onPressed;  const CustomButton({    Key? key,required this.title,required this.onPressed,  }) : super(key: key);  @override  Widget build(BuildContext context) {    return ElevatedButton(        style: ButtonStyle(            elevation: MaterialStateProperty.all(10),            minimumSize: MaterialStateProperty.all(                const Size(double.infinity, 55.5)),            backgroundColor: MaterialStateProperty.all(              Colors.white,            ),            shape: MaterialStateProperty.all<                RoundedRectangleBorder>(                RoundedRectangleBorder(                  borderRadius: BorderRadius.circular(10),                ))),        onPressed: onPressed,        child: Text(          title,          style: Theme.of(context)              .textTheme              .headline4              ?.copyWith(              color: Theme.of(context)                  .bottomAppBarColor,              fontSize: 17),        ));  }}