import 'package:easy_localization/easy_localization.dart';import 'package:flutter/material.dart';class LinkButton extends StatelessWidget {  final onPressed;  Widget? icon;  double radius;  bool cartButton;  final String title;  LinkButton({    Key? key,required this.onPressed,required this.title,this.icon,  this.cartButton = false,  this.radius = 10  }) : super(key: key);  @override  Widget build(BuildContext context) {    return SizedBox(height: 40,width: 40,child: ClipRRect      (borderRadius: BorderRadius.circular(radius),child: ColoredBox(      color:radius == 10?cartButton?Theme.of        (context).dividerColor: Theme.of(context)          .primaryColor: Colors          .grey.shade300,      child: Stack(        children: [          Center(child: icon??Text(title.tr().toString(),style:          const TextStyle            (color: Colors.white,fontSize:          27,fontWeight: FontWeight.bold),)),          ElevatedButton(              style: ButtonStyle(                  fixedSize:MaterialStateProperty.all<Size>(const Size(40,                      40),),                  minimumSize:MaterialStateProperty.all<Size>(const Size                    (40,40),),                  shadowColor:MaterialStateProperty.all(Colors.transparent),                  backgroundColor: MaterialStateProperty.all(Colors.transparent)              ),              onPressed: onPressed, child: const SizedBox.shrink())        ],      ),    ),),);  }}