import 'package:flutter/material.dart';import 'package:intex_mobile_app/core/constants/text.dart';class CartButton extends StatelessWidget {  const CartButton({    Key? key,    required this.onPressed,    this.fixSize  }) : super(key: key);  final  onPressed;  final  fixSize;  @override  Widget build(BuildContext context) {    var size = MediaQuery.of(context).size;    return ElevatedButton(      onPressed: onPressed,      style: ButtonStyle(        elevation:        MaterialStateProperty            .all(10),        minimumSize:        MaterialStateProperty            .all(fixSize??                   Size(                size.height *                    .22,                35)),        backgroundColor:        MaterialStateProperty            .all(          Theme.of(context)              .primaryColor,        ),        shape: MaterialStateProperty            .all<RoundedRectangleBorder>(          RoundedRectangleBorder(            borderRadius:            BorderRadius.circular(                10),          ),),),      child: Text(        cart,        style: Theme.of(context)            .textTheme            .headline4            ?.copyWith(        fontSize: fixSize != null ? 15:21,            color: Colors.black),      ),    );  }}