import 'package:easy_localization/easy_localization.dart';import 'package:flutter/material.dart';import 'package:intex_mobile_app/core/constants/text.dart';import 'package:intex_mobile_app/core/service/connect_check/check_servicde.dart';import 'package:intex_mobile_app/features/screens/conect_page/view/connect_check/checker/check_state.dart';class WarningWidgetValueNotifier extends StatelessWidget {  const WarningWidgetValueNotifier({Key? key}) : super(key: key);  @override  Widget build(BuildContext context) {    return ValueListenableBuilder(      valueListenable: ConnectionStatusValueNotifier(),      builder: (context, ConnectionStatus status, child) {        return Visibility(          visible: status != ConnectionStatus.online,          child: Stack(            alignment: Alignment.bottomCenter,            children: [                        SizedBox(height: double.infinity,width: double                .infinity,child: ColoredBox(color: Colors.red.withOpacity(0.4),),),            Padding(              padding: const EdgeInsets.all(16),              child: SizedBox(                height: 60,                child: ColoredBox(                  color: Colors.red,                  child: Row(                    children:  [                      const Icon(Icons.wifi_off),                      const SizedBox(width: 8),                      Text(notConnect.tr().toString()),                    ],                  ),                ),              ),            ),          ],)        );      },    );  }}