import 'dart:ui';import 'package:flutter/material.dart';  Future customAlertDialog({Key? key,required final context,required  final horizontal,    required final vertical,required final child,required final    size}) {    return showDialog(        context: context, builder:        (context){      return BackdropFilter(        filter: ImageFilter.blur(            sigmaX: 6,            sigmaY: 6        ),        child: Padding(          padding: EdgeInsets.symmetric(              vertical: MediaQuery.of(context).viewPadding.top, horizontal: horizontal),          child: SizedBox(            width: size.width,            height: size.height,            child: Dialog(              shape: const RoundedRectangleBorder(                  borderRadius: BorderRadius.all(Radius.circular(35))),              clipBehavior: Clip.hardEdge,              child: Wrap(children: [                Padding(                  padding: const EdgeInsets.only(                      right: 12),                  child: Align(                    alignment: Alignment.topRight,                    child: IconButton(                      icon: const Icon(                        Icons.close,                        color: Colors.grey,                        size: 35,                      ),                      onPressed: () {                        print('pressed close');                        Navigator.of(context)                            .pop();                      },                    ),                  ),                ),                child,              ],),),          ),        ),      );    },    );  }