import 'package:flutter/material.dart';class CustomTextField extends StatelessWidget {  const CustomTextField({    Key? key,    required this.controller,    required this.hintText, required this.keyboardType,  }) : super(key: key);  final TextEditingController controller;  final String hintText;  final TextInputType keyboardType;  @override  Widget build(BuildContext context) {    var size = MediaQuery.of(context).size;    return Container(      margin: EdgeInsets.symmetric(horizontal: size.width * .09),      decoration: BoxDecoration(          boxShadow: const [            BoxShadow(                blurRadius: 14, color: Color.fromRGBO(0, 0, 0, 0.25))          ],          borderRadius: BorderRadius.circular(17),          color: Colors.white),      child: TextField(        keyboardType: keyboardType,        controller: controller,        decoration: InputDecoration(          hintStyle: Theme.of(context)              .textTheme              .headline4              ?.copyWith(color: Colors.grey, fontSize: 20),          hintText: hintText,          contentPadding: const EdgeInsets.only(left: 10),          border: InputBorder.none,        ),      ),    );  }}