import 'package:flutter/material.dart';Future mapDialog({Key? key, required final context}) {  return showDialog(      context: context,      builder: (BuildContext context) {        return Padding(          padding: EdgeInsets.symmetric(            vertical: MediaQuery.of(context).viewPadding.top,          ),          child: Dialog(            backgroundColor: Colors.transparent,            insetPadding: const EdgeInsets.all(10),            child: Container(                width: double.infinity,                height: 310,                decoration: BoxDecoration(                    borderRadius: BorderRadius.circular(15),                    color: Theme.of(context).dividerColor),                padding: const EdgeInsets.symmetric(                    horizontal: 15, vertical: 16),                child: Container(color: Colors.red,)),          ),        );      });}