import 'package:flutter/material.dart';import 'package:flutter_svg/svg.dart';class CustomIconButton extends StatelessWidget {  final title;  final onPressed;  final icon;  const CustomIconButton({    Key? key,    required this.title,    required this.onPressed,    this.icon,  }) : super(key: key);  @override  Widget build(BuildContext context) {  bool check = icon == null;    return ElevatedButton.icon(        style: ButtonStyle(            elevation: MaterialStateProperty.all(10),            minimumSize: MaterialStateProperty.all(                const Size(double.infinity, 55.5)),            backgroundColor: MaterialStateProperty.all(             check?Theme.of(context).hoverColor: Colors.white,            ),            shape: MaterialStateProperty.all<RoundedRectangleBorder>(                RoundedRectangleBorder(                  borderRadius: BorderRadius.circular(10),                ))),        onPressed: onPressed,        icon:check?const Icon(          Icons.call,          color: Colors.white,          size: 32,        ): SvgPicture.asset(          icon,          fit: BoxFit.cover,        ),        label: Text(          title,          style: Theme.of(context)              .textTheme              .headline4              ?.copyWith(color:check?Colors.white: Theme.of(context)              .bottomAppBarColor),        ));  }}