import 'package:flutter/cupertino.dart';import 'package:flutter/material.dart';import 'package:shimmer/shimmer.dart';class PlaceHolder extends StatelessWidget {  const PlaceHolder({Key? key}) : super(key: key);  @override  Widget build(BuildContext context) {    return Shimmer.fromColors(        baseColor: Colors.grey.shade200,        highlightColor: Colors.grey.shade300,        child: const SizedBox(          height: 220,          width: 280,          child: ClipRRect(            borderRadius: BorderRadius.only(                topRight: Radius.circular(12),                topLeft: Radius.circular(12)),            child: ColoredBox(              color: Colors.grey,            ),          ),        ),);  }}