import 'package:flutter/cupertino.dart';import 'package:flutter/material.dart';ScaffoldFeatureController<SnackBar, SnackBarClosedReason>customSnackbar    ({required BuildContext context ,required String title,Colorcolor = Colors.red}){  return ScaffoldMessenger.of(context).showSnackBar(SnackBar(    backgroundColor: Colors.transparent,    elevation: 0,    content: SizedBox(      width: double.infinity,      height: 45,      child:  ClipRRect(        borderRadius: BorderRadius.circular          (12),child: ColoredBox(color:color,        child:  Align(            alignment: Alignment.center,            child: Text(title)),),      ),),    duration: const Duration(seconds: 1),  ));}