import 'package:flutter/cupertino.dart';import 'package:flutter/material.dart';ScaffoldFeatureController<SnackBar, SnackBarClosedReason>customSnackbar    ({required BuildContext context ,required String title}){  return ScaffoldMessenger.of(context).showSnackBar(SnackBar(    backgroundColor: Colors.transparent,    elevation: 0,    content: SizedBox(      width: double.infinity,      height: 45,      child:  ClipRRect(        borderRadius: BorderRadius.circular          (12),child: ColoredBox(color:      Theme.of(context).indicatorColor,        child: const Align(            alignment: Alignment.center,            child: Text('Error')),),      ),),    duration: const Duration(seconds: 5),  ));}