import 'package:easy_localization/easy_localization.dart';import 'package:flutter/cupertino.dart';import 'package:flutter/material.dart';import 'package:flutter_bloc/flutter_bloc.dart';import 'package:intex_mobile_app/core/service/connect_check/check_state.dart';import 'package:intex_mobile_app/features/screens/conect_page/bloc/connect_bloc.dart';import 'package:intex_mobile_app/features/screens/conect_page/view/connect_check/checker/check_state.dart';import 'package:intex_mobile_app/features/screens/main_page/bloc/main_bloc.dart';import 'package:intex_mobile_app/features/screens/main_page/view/main_page.dart';import '../core/repository/catalog_repository.dart';class App extends StatelessWidget {  const App({Key? key}) : super(key: key);  Future<bool> _onWillPop() async {    return false;  }  @override  Widget build(BuildContext context) {    if(ConnectionStatusValueNotifier.internetChecker.internetStatus()== 'onlin'        'e'){      return BlocProvider( create: (context) => MainBloc(            repository: Repository())..add(const MainInitialEvent()),     child: const MainPage(),);    }else{      return Scaffold(          drawerEnableOpenDragGesture: false,          backgroundColor: Theme.of(context).dividerColor,          appBar: AppBar(            backgroundColor: Theme.of(context).bottomAppBarColor,            toolbarHeight: 0,          ),          body: WillPopScope(            onWillPop:_onWillPop,            child: GestureDetector(              behavior: HitTestBehavior.opaque,              onPanDown: (_) {                FocusScope.of(context).requestFocus(FocusNode());              },              child: const Center(child: Text('s'),),            ),          ));    }    return Container();  }}