import 'dart:async';import 'package:bloc/bloc.dart';import 'package:equatable/equatable.dart';import 'package:intex_mobile_app/core/models/Catalog_model.dart';import 'package:intex_mobile_app/core/repository/product_by_status_repository.dart';import 'package:intex_mobile_app/core/service/query.dart';part 'products_event.dart';part 'products_state.dart';class ProductsBloc extends Bloc<ProductsEvent, ProductsState> {  List<Catalog> first = [];  List<Catalog> second = [];  List<Catalog> third = [];  int firstPage =-1;  int secondPage =-1;  int thirdPage =-1;  final int limit = 4;  final ProductByStatusRepository repository;  ProductsBloc({required this.repository}) : super(ProductsInitial    ()) {    on<ProductFirstGetByStatusEvent>(_getFirstData);    on<ProductFirstGetByStatusEvent>(_getSecondData);    on<ProductFirstGetByStatusEvent>(_getThirdData);  }  FutureOr<void> _getFirstData(ProductFirstGetByStatusEvent event,      Emitter<ProductsState> emit)async{    firstPage++;    QueryParams params = QueryParams();    params.append('category_id', event.id);    params.append('page', firstPage);    params.append('limit', limit);   try{  List<Catalog> list =    await repository.getByStatus(event.id,      params);     first.addAll(list);  emit(ProductFirstLoaded(first));   }catch(_){     firstPage--;     emit(ProductErrorState());   }  }  FutureOr<void> _getSecondData(ProductFirstGetByStatusEvent event,      Emitter<ProductsState> emit)async{    secondPage++;    QueryParams params = QueryParams();    params.append('category_id', event.id);    params.append('page', firstPage);    params.append('limit', limit);    try{      List<Catalog> list =    await repository.getByStatus(event.id,          params);      second.addAll(list);      emit(ProductFirstLoaded(first));    }catch(_){      secondPage--;      emit(ProductErrorState());    }  } FutureOr<void> _getThirdData(ProductFirstGetByStatusEvent event,      Emitter<ProductsState> emit)async{    thirdPage++;    QueryParams params = QueryParams();    params.append('category_id', event.id);    params.append('page', firstPage);    params.append('limit', limit);    try{      List<Catalog> list =    await repository.getByStatus(event.id,          params);      third.addAll(list);      emit(ProductFirstLoaded(first));    }catch(_){      thirdPage--;      emit(ProductErrorState());    }  }}