part of 'cart_bloc.dart';abstract class CartEvent extends Equatable {  const CartEvent();}class CartInitEvent extends CartEvent{  final Bascet bascet;  const CartInitEvent(this.bascet);  @override  List<Object?> get props => [];}class CartOrderEvent extends CartEvent{  final Bascet bascet;  const CartOrderEvent(this.bascet);  @override  List<Object?> get props => [];}class CartSendtEvent extends CartEvent{  final String name;  final String phone;  final String addres;  const CartSendtEvent({required this.name,required this.phone, required this.addres});  @override  List<Object?> get props => [];}class CartAddEvent extends CartEvent{  @override  List<Object?> get props => [];}class CartRemoveEvent extends CartEvent{  @override  List<Object?> get props => [];}class CartRequestEvent extends CartEvent{  final String name;  final String phone;  const CartRequestEvent({required this.phone,required this.name});  @override  List<Object?> get props => [];}