part of 'cart_bloc.dart';abstract class CartState extends Equatable {  const CartState();}class CartInitial extends CartState {  @override  List<Object> get props => [];}class CartLoading extends CartState {  @override  List<Object> get props => [];}class CartUpdateState extends CartState {  final Bascet bascet;  const CartUpdateState(this.bascet);  @override  List<Object> get props => [];}class CartErrorState extends CartState {  final String message;  const CartErrorState(this.message);  @override  List<Object> get props => [];}class CartSuccessState extends CartState {  @override  List<Object> get props => [];}