import 'dart:async';import 'package:bloc/bloc.dart';import 'package:equatable/equatable.dart';import 'package:intex_mobile_app/features/screens/conect_page/connect_check.dart';part 'connect_event.dart';part 'connect_state.dart';class ConnectBloc extends Bloc<ConnectEvent, ConnectState> {  ConnectBloc._() : super(NetworkInitial()) {    on<NetworkObserve>(_observe);    on<NetworkNotify>(_notifyStatus);  }  static final ConnectBloc _instance = ConnectBloc._();  factory ConnectBloc() => _instance;  void _observe(event, emit) {    NetworkHelper.observeNetwork();  }  void _notifyStatus(NetworkNotify event, emit) {    event.isConnected ? emit(NetworkSuccess()) : emit(NetworkFailure());  }}