import 'package:connectivity_plus/connectivity_plus.dart';import 'package:intex_mobile_app/features/screens/conect_page/bloc/connect_bloc.dart';class NetworkHelper {  static void observeNetwork()async {    Connectivity().onConnectivityChanged.listen((ConnectivityResult result) {      if (result == ConnectivityResult.none) {        ConnectBloc().add(const NetworkNotify());      } else {        ConnectBloc().add(const NetworkNotify(isConnected: true));      }    });  }}