import 'package:flutter/material.dart';import 'package:intex_mobile_app/features/screens/conect_page/view/connect_check/checker/check_servicde.dart';import 'package:intex_mobile_app/features/screens/conect_page/view/connect_check/checker/check_state.dart';class WarningWidgetValueNotifier extends StatelessWidget {  const WarningWidgetValueNotifier({Key? key}) : super(key: key);  @override  Widget build(BuildContext context) {    return ValueListenableBuilder(      valueListenable: ConnectionStatusValueNotifier(),      builder: (context, ConnectionStatus status, child) {        return status != ConnectionStatus.online? SliverAppBar(          actions: const [Icon(Icons              .signal_cellular_connected_no_internet_0_bar_sharp),            SizedBox(width: 10,)],          backgroundColor: Colors.red,            leading:   const Icon(Icons.wifi_off),            pinned: true,            floating: true,            title: Text('Нет соединения с интернетом',style: Theme                .of(context).textTheme.headline2?.copyWith(color:            Theme.of(context).dividerColor),)):const SliverToBoxAdapter          (child: SizedBox.shrink(),);      },    );  }}