import 'package:flutter/material.dart';class ErrorPage extends StatelessWidget {  final String message;   final onPressed;   const ErrorPage({Key? key,required this.message,this.onPressed }) :         super(key: key);  @override  Widget build(BuildContext context) {    return Scaffold(      drawerEnableOpenDragGesture: false,      backgroundColor: Theme.of(context).dividerColor,      appBar: AppBar(        backgroundColor:        Theme.of(context).bottomAppBarColor,        toolbarHeight: 0,      ),      body: Center(      child:Padding(        padding: const EdgeInsets.symmetric(horizontal: 10),        child: Column(          mainAxisAlignment: MainAxisAlignment.center,          children:[            Text(message,maxLines: 2,),            const SizedBox(height: 40,),            ElevatedButton(onPressed: ()=>onPressed , child: const            Icon(Icons.refresh))          ]  ),      ),    ),);  }}